import React, { useState, useContext } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { AuthContext } from './AuthContext';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { setUser } = useContext(AuthContext); // Get the setUser function from the AuthContext
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Assuming you have a login API call here to authenticate the user
    // For this example, we will simulate a successful login and store a dummy token in local storage
    const token = 'example_token';
    localStorage.setItem('authToken', token);

    // Update the user context with the logged-in user object
    // You can replace this with the actual user data returned from the login API call
    const user = {
      email,
      password,
    };
    setUser(user);

    history.push('/home'); // Redirect to the Home page after successful login
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      <p>New here? <Link to="/register">Register</Link></p> {/* Link to the register page */}
    </div>
  );
};

export default Login;
