import React, { useState } from 'react';
import { useHistory, Link } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Add login state
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform login API call to your backend server here
    const user = { email, password };
    console.log('Login form submitted:', user);
    // Reset form fields after submission if needed
    setEmail('');
    setPassword('');

    // Simulating successful login, you should call your login API here and handle the response accordingly
    // For now, we'll assume login is successful
    // Set login state to true
    setIsLoggedIn(true);
    // Redirect to the Home page
    history.push('/');
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New here? <Link to="/register">Register</Link></p> {/* Use Link component */}
      </form>
    </div>
  );
};

export default Login;
