import React, { useEffect, useContext } from 'react';
import Navbar from './Navbar';
import Home from './Home';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Create from './Create';
import BlogDetails from './BlogDetails';
import NotFound from './NotFound';
import Login from './Login';
import Register from './Register';
import { AuthContext } from './AuthContext';
import { useHistory } from 'react-router-dom'; // Import the useHistory hook

function App() {
  const { user } = useContext(AuthContext);
  const history = useHistory(); // Initialize the useHistory hook

  useEffect(() => {
    const authToken = localStorage.getItem('authToken');
    // Check if the user is logged in by checking the token in local storage
    // You can modify this condition as needed based on how you store your token
    if (authToken) {
      // If the user is logged in, redirect to the Home page
      history.push('/home');
    }
  }, [user, history]); // Add history to the dependencies array

  return (
    <Router>
      <div className="App">
        <Navbar />
        <div className="content">
          <Switch>
            <Route exact path="/">
              {user ? <Redirect to="/home" /> : <Login />}
            </Route>
            <Route path="/home">
              <Home />
            </Route>
            <Route path="/create">
              <Create />
            </Route>
            <Route path="/blogs/:id">
              <BlogDetails />
            </Route>
            <Route path="/login">
              <Login />
            </Route>
            <Route path="/register">
              <Register />
            </Route>
            <Route path="*">
              <NotFound />
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
