import React, { useState, useContext } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { AuthContext } from './AuthContext';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { setUser } = useContext(AuthContext); 
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();

    const loginData = {
      username,
      password,
    };

    fetch('http://localhost:8000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(loginData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.m_id && data.name && data.username) {
          const user = { m_id: data.m_id, name: data.name, username: data.username };
          const token = 'example_token'; // Replace this with the actual token from the response
          localStorage.setItem('authToken', token); // Store the token in local storage
          setUser(user); // Update the user context with the logged-in user object
          history.push('/home');
        } else {
          alert('Invalid username or password. Please try again.');
        }
      })
      .catch((error) => {
        console.error('Error logging in:', error);
        // Handle error state if necessary
      });
  };




  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>username:</label>
        <input
          type="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      <p>New here? <Link to="/register">Register</Link></p> {/* Link to the register page */}
    </div>
  );
};

export default Login;
