import React, { useState, useContext, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { AuthContext } from './AuthContext';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userNotFound, setUserNotFound] = useState(false); // State to track if the user is not found
  const { setUser } = useContext(AuthContext);
  const history = useHistory();

  useEffect(() => {
    // Fetch the members table data to check if the entered username exists
    fetch(`http://localhost:8000/members/${username}`)
      .then((response) => response.json())
      .then((data) => {
        const userExists = data.some((member) => member.username === username);
        setUserNotFound(!userExists); // Set userNotFound to true if the username doesn't exist
      })
      .catch((error) => {
        console.error('Error fetching members data:', error);
      });
  }, [username]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Proceed with the login if the user is found in the members table
    if (!userNotFound) {
      const user = { username, password }; // Replace this with your login data
      const token = 'example_token'; // Replace this with the actual token from the response
      localStorage.setItem('authToken', token); // Store the token in local storage
      setUser(user); // Update the user context with the logged-in user object
      history.push('/home');
    } else {
      // Display a message to register if the user is not found
      alert('User not found. Please register first.');
    }
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>Username:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
