// GlitterSparkle.js
import React, { useEffect, useState } from 'react';
import './GlitterSparkle.css';

const GlitterSparkle = () => {
  const [firstVisit, setFirstVisit] = useState(false);

  useEffect(() => {
    // Check if this is the user's first visit
    const visited = localStorage.getItem('visited');
    if (!visited) {
      // Mark the user as visited and trigger the glitter effect
      localStorage.setItem('visited', 'true');
      setFirstVisit(true);

      // After 2 seconds, remove the glitter effect
      setTimeout(() => {
        setFirstVisit(false);
      }, 2000);
    }
  }, []);
  console.log("iwas")

  return (
    <div className={`glitter-container ${firstVisit ? 'glitter-active' : ''}`}>
      {Array.from({ length: 50 }).map((_, index) => (
        <div key={index} className="glitter"></div>
      ))}
    </div>
  );
};

export default GlitterSparkle;
