import React, { useState, useEffect, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { AuthContext } from './AuthContext';

const Create = () => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [authorId, setAuthorId] = useState('');
  const { user } = useContext(AuthContext);
  const history = useHistory();

  useEffect(() => {
    fetch(`http://localhost:8000/members/${user.username}`) // Replace with the actual endpoint for fetching a specific member using the username
      .then((response) => response.json())
      .then((data) => {
        setAuthorId(data.m_id);
      })
      .catch((error) => {
        console.error('Error fetching member data:', error);
      });
  }, [user.username]);
  const handleSubmit = (e) => {
    e.preventDefault();
    const blog = {
      title,
      body,
      author: user.username,
      author_id: user.m_id,
    };

    fetch('http://localhost:8000/blogs', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    })
    .then((response) => response.json())
    .then((data) => {
      console.log(data); // You can get the inserted blog's ID from the response data if needed
      history.push('/home');
    })
    .catch((error) => {
      console.error('Error creating blog:', error);
      // Handle error state if necessary
    });
  }
  console.log(user);

  return (
    <div className="create">
      <h2>Add a New Blog</h2>
      <form onSubmit={handleSubmit}>
        <label>Blog title:</label>
        <input
          type="text"
          required
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Blog body:</label>
        <textarea
          required
          value={body}
          onChange={(e) => setBody(e.target.value)}
        ></textarea>
        <button>Add Blog</button>
      </form>
      <div className="preview">
        <h1>Preview</h1>
        <h2>{title}</h2>
        {user ? (
          <p>Written by {user.username}</p>
        ) : (
          <p>Written by Anonymous</p>
        )}
        <div>{body}</div>
      </div>
    </div>
  );
}

export default Create;
