import React, { useState, useContext } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { AuthContext } from './AuthContext';

const Login = () => {
  const [username, setusername] = useState('');
  const [password, setPassword] = useState('');
  const { setUser } = useContext(AuthContext); // Get the setUser function from the AuthContext
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();

    const user = {username, password }; // Replace this with your login data
    const token = 'example_token'; // Replace this with the actual token from the response
    localStorage.setItem('authToken', token); // Store the token in local storage

    setUser(user); // Update the user context with the logged-in user object
    history.push('/home'); 
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>username:</label>
        <input
          type="username"
          value={username}
          onChange={(e) => setusername(e.target.value)}
          required
        />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      <p>New here? <Link to="/register">Register</Link></p> {/* Link to the register page */}
    </div>
  );
};

export default Login;
